pragma solidity ^0.4.23;

contract MyCoin {
	string public name;
	string public symbol;
	string public standard;
	address public admin;
	bool public isAdminSet = false;

	mapping (address => uint) public balanceOf;

	mapping (uint => transaction) public allTransactions;
	uint public allTransactions_count;

	mapping (uint => transaction) public transfer;
	uint public transfer_count;

	struct transaction {
	  address from;
	  uint value;
	  address to;
	  int status;
	  uint txnId;
	}
	
	function MyCoin(uint _initialSupply) public{
		name = "My Coin";
		symbol = "MyCoin";
		standard = "My Coin V1.0";
		//balanceOf[msg.sender] = _initialSupply;
		//admin = msg.sender;
		allTransactions_count = 0;
		transfer_count = 0;
	}

	function setAdmin(){
		require(isAdminSet==false);
		balanceOf[msg.sender] = 1000000;
		admin = msg.sender;
		isAdminSet = true;
	}

	function reset() public{
		require(msg.sender==admin);
		allTransactions_count = 0;
		transfer_count = 0;
	}

	function refillCoin() public{
		require(msg.sender==admin);
		balanceOf[admin] = 1000000;
	}

	function changeAdmin(address _newAdmin) public{
		require(msg.sender==admin);
		require(_newAdmin!=admin);
		balanceOf[_newAdmin] = balanceOf[admin];
		balanceOf[admin] = 0;
	}

	function transferCoin(uint _value,address _to) public{
		require (_value>0);
		require (balanceOf[msg.sender]>=_value);
		balanceOf[msg.sender] -= _value;
		balanceOf[_to] += _value;
		transfer[transfer_count] = transaction(msg.sender,_value,_to,1,transfer_count);
		transfer_count++;
		//TransferCoin(msg.sender,_value,_to);
	}

	function requestCoin(uint _value) public{
		require(msg.sender!=admin);
		require (_value>0);
		require (balanceOf[admin]>=_value);
		allTransactions[allTransactions_count] = transaction(admin,_value,msg.sender,0,allTransactions_count);
		allTransactions_count++;
		//RequestCoin(msg.sender,_value);
	}

	function acceptTransaction(uint _no) public{
		require(msg.sender==admin);
		require(_no>=0);
		require(_no<=allTransactions_count);
		require(allTransactions[_no].value>0);
		require(allTransactions[_no].status==0);
		require(balanceOf[admin]>=allTransactions[_no].value);

		balanceOf[allTransactions[_no].to] += allTransactions[_no].value;
		balanceOf[admin] -= allTransactions[_no].value;
		allTransactions[_no].status = 1;
	}

	function rejectTransaction(uint _no) public{
		require(msg.sender==admin);
		require(_no>=0);
		require(_no<=allTransactions_count);
		require(allTransactions[_no].status==0);
		allTransactions[_no].status = -1;
	}
	
}
